void Cob4BaseInit::startAllTasks() {
	// start task SendInitRequest_activity
	if(connections.sendInitRequest_activity.scheduler != "DEFAULT") {
		ACE_Sched_Params sendInitRequest_activity_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.sendInitRequest_activity.scheduler == "FIFO") {
			sendInitRequest_activity_SchedParams.policy(ACE_SCHED_FIFO);
			sendInitRequest_activity_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.sendInitRequest_activity.scheduler == "RR") {
			sendInitRequest_activity_SchedParams.policy(ACE_SCHED_RR);
			sendInitRequest_activity_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		sendInitRequest_activity->start(sendInitRequest_activity_SchedParams, connections.sendInitRequest_activity.cpuAffinity);
	} else {
		sendInitRequest_activity->start();
	}
}

